import React, {useState} from 'react'
import axios from 'axios';
import { useAuth } from '../context/auth';
import { useNavigate } from 'react-router-dom';

export default function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [auth, setAuth] = useAuth();
    const navigate = useNavigate();

    

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await axios.post(
                'http://localhost:8000/login/',  // Update with your backend URL
                { email, password }, {
                    headers: {
                        'Content-Type': 'application/json', // Make sure this matches your backend expectations
                        // Include any other necessary headers here
                    },
                }
            );

            console.log('Login successful:', response.data.message);
            setAuth({
                ...auth,
                user: response.data.user
              });
              localStorage.setItem('auth', JSON.stringify(response.data))
              navigate('/');
        } catch (error) {
            setError('Invalid login credentials');
            console.error('Login error:', error);
            console.log('Axios error details:', error.response); // Log the error response for more information
            setError('')
        }
    };
  return (
    <div className='AuthSection'>
       <div className="AuthImage">
           <img src="/images/bg.jpg" alt="" />
       </div>
       <div className="AuthForm">
           <div className="Heading">
              <h1>LOGIN</h1>
           </div>
           <div className="Form">
              <form onSubmit={handleLogin}>
                  <div className="mb">
                      <label htmlFor="Email">Email</label>
                      <input type="text" name='email' onChange={(e) => setEmail(e.target.value)} />
                  </div>
                  <div className="mb">
                      <label htmlFor="Password">Password</label>
                      <input type="password" name='password' onChange={(e) => setPassword(e.target.value)} />
                  </div>
                  <div className="mb">
                      <input className='btn' type="submit" value="Login" />
                  </div>
              </form>
           </div>
       </div>
    </div>
  )
}
